package ss6_inheritance.thuc_hanh.geometric_object;

public class Test {
    public static void main(String[] args) {
        Shape shape = new Shape();//cha của Circle và Rectangle
        Circle circle = new Circle(); //con của Shape
        Rectangle rectangle = new Rectangle(); //con của Square và cha của Square
        Square square = new Square(); // con của Rectangle
        //    ------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------

        System.out.println(shape);
        shape = new Shape("blue", false);
        System.out.println(shape);
        //    ------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------


        System.out.println(circle);
        circle = new Circle("red", false, 3.5);
        System.out.println(circle);
        //    ------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------

        System.out.println(rectangle);
        rectangle = new Rectangle("Green", true, 3, 4);
        System.out.println(rectangle);
        //    ------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------

        System.out.println(square);
        square = new Square("Green", true, 3);
        System.out.println(square);

    }
}
